import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Select from 'react-select';
import UniversityCardList from './UniversityCard1';

export default function Stats() {
  const [institutes, setInstitutes] = useState([]);
  const [categories, setCategories] = useState([]);
  const [courses, setCourses] = useState([]);
  const [quotas, setQuotas] = useState([]);  // New state for quotas
  const [selectedInstitute, setSelectedInstitute] = useState(null);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedQuota, setSelectedQuota] = useState(null);  // New state for selected quota
  const [minRank, setMinRank] = useState('');
  const [universities, setUniversities] = useState([]);

  useEffect(() => {
    async function fetchFilters() {
      try {
        const response = await axios.get('http://localhost:4000/api/filters1');
        setInstitutes(response.data.institutes.map(inst => ({ value: inst, label: inst })));
        setCategories(response.data.categories.map(cat => ({ value: cat, label: cat })));
        setCourses(response.data.courses.map(course => ({ value: course, label: course })));
        setQuotas(response.data.quotas.map(quota => ({ value: quota, label: quota })));  // Set quotas
      } catch (error) {
        console.error('Error fetching filters:', error);
      }
    }

    fetchFilters();
  }, []);

  const handleSearch = async () => {
    try {
      const response = await axios.post('http://localhost:4000/api/predict1', {
        institute: selectedInstitute?.value,
        course: selectedCourse?.value,
        category: selectedCategory?.value,
        quota: selectedQuota?.value,  // Include quota in search
        minRank: minRank,
      });
      console.log('Fetched universities:', response.data);
      setUniversities(response.data); // Store the returned universities in state
    } catch (error) {
      console.error('Error fetching universities:', error);
    }
  };

  return (
    <>
      <section className="stats_box py-10 grid place-items-center lg:grid-cols-4 grid-cols-2 gap-5 sm:w-9/12 w-11/12 mx-auto -mt-9 px-6">

        <div className="w-full">
          <Select
            options={courses}
            placeholder="Select Course"
            classNamePrefix="react-select"
            onChange={setSelectedCourse}
          />
        </div>

        <div className="w-full">
          <Select
            options={categories}
            placeholder="Select Category"
            classNamePrefix="react-select"
            onChange={setSelectedCategory}
          />
        </div>

        <div className="w-full">
          <Select
            options={quotas}  // New Select input for quotas
            placeholder="Select Quota"
            classNamePrefix="react-select"
            onChange={setSelectedQuota}
          />
        </div>

        <div className="w-full">
          <input
            type="text"
            placeholder="Enter Min Rank"
            className="md:text-[15px] text-[12px] font-bold p-2 border border-gray-300 rounded w-full"
            value={minRank}
            onChange={(e) => setMinRank(e.target.value)}
          />
        </div>

        <div className="w-full">
          <button
            className="md:text-[15px] text-[12px] font-bold p-2 bg-orange-500 text-white rounded w-full"
            onClick={handleSearch}
          >
            Search
          </button>
        </div>
      </section>

      <UniversityCardList universities={universities} />
    </>
  );
}
