import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = () => {
    const [isRightPanelActive, setIsRightPanelActive] = useState(false);
    const [signUpData, setSignUpData] = useState({ username: '', email: '', password: '', mobile: '' });
    const [signInData, setSignInData] = useState({ email: '', password: '' });
    const [errors, setErrors] = useState({ signUp: {}, signIn: {} });
    const navigate = useNavigate();

    const validateEmail = (email) => {
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return regex.test(email);
    };

    const validateMobile = (mobile) => {
        return /^[0-9]{10}$/.test(mobile);
    };

    const validateSignUp = () => {
        let errors = {};
        if (!signUpData.username.trim()) errors.username = "Username is required";
        if (!signUpData.email.trim()) errors.email = "Email is required";
        else if (!validateEmail(signUpData.email)) errors.email = "Invalid email format";
        if (!signUpData.password) errors.password = "Password is required";
        else if (signUpData.password.length < 6) errors.password = "Password must be at least 6 characters";
        if (!validateMobile(signUpData.mobile)) errors.mobile = "Invalid mobile number. Must be 10 digits.";
        return errors;
    };

    const validateSignIn = () => {
        let errors = {};
        if (!signInData.email.trim()) errors.email = "Email is required";
        else if (!validateEmail(signInData.email)) errors.email = "Invalid email format";
        if (!signInData.password) errors.password = "Password is required";
        return errors;
    };

    const handleSignUpClick = () => {
        setIsRightPanelActive(true);
    };

    const handleSignInClick = () => {
        setIsRightPanelActive(false);
    };

    const handleSignUpChange = (e) => {
        setSignUpData({ ...signUpData, [e.target.name]: e.target.value });
    };

    const handleSignInChange = (e) => {
        setSignInData({ ...signInData, [e.target.name]: e.target.value });
    };

    const handleSignUpSubmit = async (e) => {
        e.preventDefault();
        const validationErrors = validateSignUp();
        if (Object.keys(validationErrors).length === 0) {
            try {
                const response = await fetch('http://localhost:4000/addUser', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(signUpData),
                });
                const result = await response.json();
                console.log(result);
                if (result.operation === 'success') {
                    alert('Sign Up Successful');
                } else {
                    alert('Sign Up Failed');
                }
            } catch (error) {
                console.error('Error during sign-up:', error);
            }
        } else {
            setErrors({ ...errors, signUp: validationErrors });
        }
    };

    const handleSignInSubmit = async (e) => {
        e.preventDefault();
        const validationErrors = validateSignIn();
        if (Object.keys(validationErrors).length === 0) {
            try {
                const response = await fetch('http://localhost:4000/login-by-post', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(signInData),
                });

                if (response.ok) {
                    const result = await response.json();
                    console.log(result);
                    if (response.status === 200) {
                        alert('Login Successful');
                        navigate('/');
                    } else {
                        alert('Login Failed');
                    }
                } else {
                    console.error('Login Error:', response.statusText);
                    alert('Login Failed');
                }
            } catch (error) {
                console.error('Error during login:', error);
            }
        } else {
            setErrors({ ...errors, signIn: validationErrors });
        }
    };

    return (
        <div className={`login-container ${isRightPanelActive ? 'right-panel-active' : ''}`}>
            <div className="form-container sign-up-container">
                <form onSubmit={handleSignUpSubmit}>
                    <h1>Create Account</h1>
                    <input type="text" name="username" placeholder="Username" value={signUpData.username} onChange={handleSignUpChange} />
                    {errors.signUp.username && <span className="error">{errors.signUp.username}</span>}
                    
                    <input type="email" name="email" placeholder="Email" value={signUpData.email} onChange={handleSignUpChange} />
                    {errors.signUp.email && <span className="error">{errors.signUp.email}</span>}

                    <input type="password" name="password" placeholder="Password" value={signUpData.password} onChange={handleSignUpChange} />
                    {errors.signUp.password && <span className="error">{errors.signUp.password}</span>}

                    <input type="text" name="mobile" placeholder="Mobile" value={signUpData.mobile} onChange={handleSignUpChange} />
                    {errors.signUp.mobile && <span className="error">{errors.signUp.mobile}</span>}

                    <button type="submit">Sign Up</button>
                </form>
            </div>
            
            <div className="form-container sign-in-container">
                <form onSubmit={handleSignInSubmit}>
                    <h1>Sign in</h1>
                    <input type="email" name="email" placeholder="Email" value={signInData.email} onChange={handleSignInChange} />
                    {errors.signIn.email && <span className="error">{errors.signIn.email}</span>}

                    <input type="password" name="password" placeholder="Password" value={signInData.password} onChange={handleSignInChange} />
                    {errors.signIn.password && <span className="error">{errors.signIn.password}</span>}

                    <button type="submit">Sign In</button>
                </form>
            </div>
        </div>
    );
};

export default LoginForm;
